     1â†’/**
     2â†’ * OBS Connection Manager
     3â†’ *
     4â†’ * Manages OBS WebSocket connections to competition VMs.
     5â†’ * When a client connects for a specific competition, this manager
     6â†’ * connects to the OBS instance running on that competition's VM.
     7â†’ *
     8â†’ * This solves the Mixed Content issue where HTTPS frontends cannot
     9â†’ * connect directly to HTTP WebSockets on VMs.
    10â†’ *
    11â†’ * @module obsConnectionManager
    12â†’ */
    13â†’
    14â†’import OBSWebSocket, { EventSubscription } from 'obs-websocket-js';
    15â†’import { EventEmitter } from 'events';
    16â†’
    17â†’/**
    18â†’ * Manages OBS connections per competition
    19â†’ */
    20â†’class OBSConnectionManager extends EventEmitter {
    21â†’  constructor() {
    22â†’    super();
    23â†’
    24â†’    // Map of compId -> OBSWebSocket instances
    25â†’    this.connections = new Map();
    26â†’
    27â†’    // Map of compId -> connection state
    28â†’    this.connectionStates = new Map();
    29â†’
    30â†’    // Reconnect timers per competition
    31â†’    this.reconnectTimers = new Map();
    32â†’
    33â†’    // Heartbeat timers per competition
    34â†’    this.heartbeatTimers = new Map();
    35â†’
    36â†’    // Heartbeat configuration
    37â†’    this.HEARTBEAT_INTERVAL = 15000; // 15 seconds
    38â†’    this.HEARTBEAT_TIMEOUT = 5000; // 5 seconds to respond
    39â†’
    40â†’    // Audio level subscription tracking
    41â†’    this.audioLevelSubscriptions = new Map(); // compId -> Set<socketId>
    42â†’    this.audioLevelHandlers = new Map(); // compId -> handler function
    43â†’    this.AUDIO_LEVEL_THROTTLE_MS = 66; // ~15fps (OBS sends at ~60fps)
    44â†’
    45â†’    // Default OBS WebSocket port
    46â†’    this.OBS_PORT = 4455;
    47â†’
    48â†’    // Default password (can be overridden)
    49â†’    this.OBS_PASSWORD = process.env.OBS_WEBSOCKET_PASSWORD || '';
    50â†’
    51â†’    console.log('[OBSConnectionManager] Initialized');
    52â†’  }
    53â†’
    54â†’  /**
    55â†’   * Connect to OBS on a specific VM for a competition
    56â†’   * @param {string} compId - Competition ID
    57â†’   * @param {string} vmAddress - VM address (IP or hostname)
    58â†’   * @returns {Promise<OBSWebSocket>} The OBS WebSocket connection
    59â†’   */
    60â†’  async connectToVM(compId, vmAddress) {
    61â†’    console.log(`[OBSConnectionManager] Connecting to OBS for competition ${compId} on VM ${vmAddress}`);
    62â†’
    63â†’    // Check if already connected
    64â†’    if (this.isConnected(compId)) {
    65â†’      console.log(`[OBSConnectionManager] Already connected to OBS for ${compId}`);
    66â†’      return this.connections.get(compId);
    67â†’    }
    68â†’
    69â†’    // Parse vmAddress - it might be "ip:port" or just "ip"
    70â†’    let host = vmAddress;
    71â†’    if (vmAddress.includes(':')) {
    72â†’      // vmAddress format is "ip:serverPort" (e.g., "3.89.92.162:3003")
    73â†’      // We need to use the OBS WebSocket port (4455), not the server port
    74â†’      host = vmAddress.split(':')[0];
    75â†’    }
    76â†’
    77â†’    const obsUrl = `ws://${host}:${this.OBS_PORT}`;
    78â†’    console.log(`[OBSConnectionManager] OBS WebSocket URL: ${obsUrl}`);
    79â†’
    80â†’    // Create new OBS WebSocket instance
    81â†’    const obs = new OBSWebSocket();
    82â†’
    83â†’    // Set up event handlers
    84â†’    this._setupEventHandlers(obs, compId, vmAddress);
    85â†’
    86â†’    try {
    87â†’      // Connect to OBS with InputVolumeMeters high-volume event subscription
    88â†’      // InputVolumeMeters is NOT included in EventSubscription.All - must be explicitly requested
    89â†’      await obs.connect(obsUrl, this.OBS_PASSWORD || undefined, {
    90â†’        eventSubscriptions: EventSubscription.All | EventSubscription.InputVolumeMeters,
    91â†’        rpcVersion: 1
    92â†’      });
    93â†’
    94â†’      // Store connection
    95â†’      this.connections.set(compId, obs);
    96â†’      this.connectionStates.set(compId, {
    97â†’        connected: true,
    98â†’        vmAddress: vmAddress,
    99â†’        connectedAt: new Date().toISOString(),
   100â†’        error: null
   101â†’      });
   102â†’
   103â†’      console.log(`[OBSConnectionManager] Connected to OBS for ${compId} at ${obsUrl}`);
   104â†’
   105â†’      // Start heartbeat monitoring
   106â†’      this._startHeartbeat(compId);
   107â†’
   108â†’      // Emit connection event
   109â†’      this.emit('connected', { compId, vmAddress });
   110â†’
   111â†’      return obs;
   112â†’    } catch (error) {
   113â†’      console.error(`[OBSConnectionManager] Failed to connect to OBS for ${compId}:`, error.message);
   114â†’
   115â†’      // Update connection state
   116â†’      this.connectionStates.set(compId, {
   117â†’        connected: false,
   118â†’        vmAddress: vmAddress,
   119â†’        connectedAt: null,
   120â†’        error: error.message
   121â†’      });
   122â†’
   123â†’      // Schedule reconnect
   124â†’      this._scheduleReconnect(compId, vmAddress);
   125â†’
   126â†’      throw error;
   127â†’    }
   128â†’  }
   129â†’
   130â†’  /**
   131â†’   * Get OBS connection for a competition
   132â†’   * @param {string} compId - Competition ID
   133â†’   * @returns {OBSWebSocket|null} The OBS connection or null
   134â†’   */
   135â†’  getConnection(compId) {
   136â†’    return this.connections.get(compId) || null;
   137â†’  }
   138â†’
   139â†’  /**
   140â†’   * Check if connected to OBS for a competition
   141â†’   * @param {string} compId - Competition ID
   142â†’   * @returns {boolean} True if connected
   143â†’   */
   144â†’  isConnected(compId) {
   145â†’    const state = this.connectionStates.get(compId);
   146â†’    return state?.connected === true;
   147â†’  }
   148â†’
   149â†’  /**
   150â†’   * Get connection state for a competition
   151â†’   * @param {string} compId - Competition ID
   152â†’   * @returns {Object|null} Connection state or null
   153â†’   */
   154â†’  getConnectionState(compId) {
   155â†’    return this.connectionStates.get(compId) || null;
   156â†’  }
   157â†’
   158â†’  /**
   159â†’   * Disconnect from OBS for a competition
   160â†’   * @param {string} compId - Competition ID
   161â†’   */
   162â†’  async disconnect(compId) {
   163â†’    console.log(`[OBSConnectionManager] Disconnecting from OBS for ${compId}`);
   164â†’
   165â†’    // Stop heartbeat
   166â†’    this._stopHeartbeat(compId);
   167â†’
   168â†’    // Clear reconnect timer
   169â†’    const timer = this.reconnectTimers.get(compId);
   170â†’    if (timer) {
   171â†’      clearTimeout(timer);
   172â†’      this.reconnectTimers.delete(compId);
   173â†’    }
   174â†’
   175â†’    // Disconnect OBS
   176â†’    const obs = this.connections.get(compId);
   177â†’    if (obs) {
   178â†’      try {
   179â†’        await obs.disconnect();
   180â†’      } catch (e) {
   181â†’        // Ignore disconnect errors
   182â†’      }
   183â†’      this.connections.delete(compId);
   184â†’    }
   185â†’
   186â†’    // Update state
   187â†’    this.connectionStates.set(compId, {
   188â†’      connected: false,
   189â†’      vmAddress: this.connectionStates.get(compId)?.vmAddress,
   190â†’      connectedAt: null,
   191â†’      error: 'Disconnected'
   192â†’    });
   193â†’
   194â†’    this.emit('disconnected', { compId });
   195â†’  }
   196â†’
   197â†’  /**
   198â†’   * Set up event handlers for an OBS connection
   199â†’   * @private
   200â†’   */
   201â†’  _setupEventHandlers(obs, compId, vmAddress) {
   202â†’    // Connection closed
   203â†’    obs.on('ConnectionClosed', () => {
   204â†’      console.log(`[OBSConnectionManager] OBS connection closed for ${compId}`);
   205â†’
   206â†’      // Stop heartbeat
   207â†’      this._stopHeartbeat(compId);
   208â†’
   209â†’      this.connectionStates.set(compId, {
   210â†’        connected: false,
   211â†’        vmAddress: vmAddress,
   212â†’        connectedAt: null,
   213â†’        error: 'Connection closed'
   214â†’      });
   215â†’
   216â†’      this.emit('connectionClosed', { compId });
   217â†’
   218â†’      // Schedule reconnect
   219â†’      this._scheduleReconnect(compId, vmAddress);
   220â†’    });
   221â†’
   222â†’    // Connection error
   223â†’    obs.on('ConnectionError', (error) => {
   224â†’      console.error(`[OBSConnectionManager] OBS connection error for ${compId}:`, error);
   225â†’
   226â†’      this.connectionStates.set(compId, {
   227â†’        connected: false,
   228â†’        vmAddress: vmAddress,
   229â†’        connectedAt: null,
   230â†’        error: error.message
   231â†’      });
   232â†’
   233â†’      this.emit('connectionError', { compId, error: error.message });
   234â†’    });
   235â†’
   236â†’    // Identified (successfully authenticated)
   237â†’    obs.on('Identified', () => {
   238â†’      console.log(`[OBSConnectionManager] OBS identified for ${compId}`);
   239â†’
   240â†’      this.connectionStates.set(compId, {
   241â†’        connected: true,
   242â†’        vmAddress: vmAddress,
   243â†’        connectedAt: new Date().toISOString(),
   244â†’        error: null
   245â†’      });
   246â†’
   247â†’      this.emit('identified', { compId });
   248â†’    });
   249â†’
   250â†’    // Forward OBS events to listeners
   251â†’    const eventsToForward = [
   252â†’      'CurrentProgramSceneChanged',
   253â†’      'SceneListChanged',
   254â†’      'InputVolumeChanged',
   255â†’      'InputMuteStateChanged',
   256â†’      'StreamStateChanged',
   257â†’      'RecordStateChanged',
   258â†’      'StudioModeStateChanged',
   259â†’      'CurrentPreviewSceneChanged'
   260â†’    ];
   261â†’
   262â†’    for (const eventName of eventsToForward) {
   263â†’      obs.on(eventName, (data) => {
   264â†’        this.emit('obsEvent', { compId, eventName, data });
   265â†’      });
   266â†’    }
   267â†’  }
   268â†’
   269â†’  /**
   270â†’   * Schedule a reconnection attempt
   271â†’   * @private
   272â†’   */
   273â†’  _scheduleReconnect(compId, vmAddress) {
   274â†’    // Clear existing timer
   275â†’    const existingTimer = this.reconnectTimers.get(compId);
   276â†’    if (existingTimer) {
   277â†’      clearTimeout(existingTimer);
   278â†’    }
   279â†’
   280â†’    // Schedule reconnect after 30 seconds
   281â†’    const timer = setTimeout(async () => {
   282â†’      console.log(`[OBSConnectionManager] Attempting reconnection for ${compId}`);
   283â†’      try {
   284â†’        await this.connectToVM(compId, vmAddress);
   285â†’      } catch (error) {
   286â†’        console.error(`[OBSConnectionManager] Reconnection failed for ${compId}:`, error.message);
   287â†’        // _scheduleReconnect will be called again from the error handler
   288â†’      }
   289â†’    }, 30000);
   290â†’
   291â†’    this.reconnectTimers.set(compId, timer);
   292â†’  }
   293â†’
   294â†’  /**
   295â†’   * Start heartbeat monitoring for a connection
   296â†’   * Pings OBS every HEARTBEAT_INTERVAL ms to detect dead connections
   297â†’   * @private
   298â†’   */
   299â†’  _startHeartbeat(compId) {
   300â†’    // Stop any existing heartbeat
   301â†’    this._stopHeartbeat(compId);
   302â†’
   303â†’    const obs = this.connections.get(compId);
   304â†’    if (!obs) {
   305â†’      return;
   306â†’    }
   307â†’
   308â†’    console.log(`[OBSConnectionManager] Starting heartbeat for ${compId} (interval: ${this.HEARTBEAT_INTERVAL}ms)`);
   309â†’
   310â†’    const heartbeat = setInterval(async () => {
   311â†’      const currentObs = this.connections.get(compId);
   312â†’      if (!currentObs || !this.isConnected(compId)) {
   313â†’        this._stopHeartbeat(compId);
   314â†’        return;
   315â†’      }
