   333â†’        // Stop heartbeat before triggering disconnect
   334â†’        this._stopHeartbeat(compId);
   335â†’
   336â†’        // Get vmAddress for reconnection
   337â†’        const state = this.connectionStates.get(compId);
   338â†’        const vmAddress = state?.vmAddress;
   339â†’
   340â†’        // Update connection state
   341â†’        this.connectionStates.set(compId, {
   342â†’          connected: false,
   343â†’          vmAddress: vmAddress,
   344â†’          connectedAt: null,
   345â†’          error: 'Connection lost (heartbeat failed)'
