   376â†’  }
   377â†’
   378â†’  // =============================================
   379â†’  // Audio Level Subscription Methods (Phase 2)
   380â†’  // =============================================
   381â†’
   382â†’  /**
   383â†’   * Subscribe a socket to receive audio level updates for a competition
   384â†’   * @param {string} compId - Competition ID
   385â†’   * @param {string} socketId - Socket ID of the subscriber
   386â†’   */
   387â†’  subscribeAudioLevels(compId, socketId) {
   388â†’    if (!this.audioLevelSubscriptions.has(compId)) {
   389â†’      this.audioLevelSubscriptions.set(compId, new Set());
   390â†’    }
   391â†’    this.audioLevelSubscriptions.get(compId).add(socketId);
   392â†’
   393â†’    console.log(`[OBSConnectionManager] Audio level subscription added: ${socketId} -> ${compId} (total: ${this.audioLevelSubscriptions.get(compId).size})`);
   394â†’
   395â†’    // Start forwarding if this is the first subscriber
   396â†’    if (this.audioLevelSubscriptions.get(compId).size === 1) {
   397â†’      this._startAudioLevelForwarding(compId);
   398â†’    }
   399â†’  }
   400â†’
   401â†’  /**
   402â†’   * Unsubscribe a socket from audio level updates
   403â†’   * @param {string} compId - Competition ID
   404â†’   * @param {string} socketId - Socket ID to unsubscribe
   405â†’   */
   406â†’  unsubscribeAudioLevels(compId, socketId) {
   407â†’    const subs = this.audioLevelSubscriptions.get(compId);
   408â†’    if (subs) {
   409â†’      subs.delete(socketId);
   410â†’      console.log(`[OBSConnectionManager] Audio level subscription removed: ${socketId} -> ${compId} (remaining: ${subs.size})`);
   411â†’
   412â†’      // Stop forwarding if no more subscribers
   413â†’      if (subs.size === 0) {
   414â†’        this._stopAudioLevelForwarding(compId);
   415â†’        this.audioLevelSubscriptions.delete(compId);
   416â†’      }
   417â†’    }
   418â†’  }
   419â†’
   420â†’  /**
   421â†’   * Unsubscribe a socket from all competitions (called on disconnect)
   422â†’   * @param {string} socketId - Socket ID to unsubscribe
   423â†’   */
   424â†’  unsubscribeAudioLevelsAll(socketId) {
   425â†’    for (const [compId, subs] of this.audioLevelSubscriptions) {
   426â†’      if (subs.has(socketId)) {
   427â†’        this.unsubscribeAudioLevels(compId, socketId);
   428â†’      }
   429â†’    }
   430â†’  }
   431â†’
   432â†’  /**
   433â†’   * Start forwarding audio levels from OBS to subscribers
