   435â†’   * @private
   436â†’   */
   437â†’  _startAudioLevelForwarding(compId) {
   438â†’    const obs = this.connections.get(compId);
   439â†’    if (!obs) {
   440â†’      console.warn(`[OBSConnectionManager] Cannot start audio forwarding: no connection for ${compId}`);
   441â†’      return;
   442â†’    }
   443â†’
   444â†’    // Don't start if already forwarding
   445â†’    if (this.audioLevelHandlers.has(compId)) {
   446â†’      return;
   447â†’    }
   448â†’
   449â†’    console.log(`[OBSConnectionManager] Starting audio level forwarding for ${compId} (throttle: ${this.AUDIO_LEVEL_THROTTLE_MS}ms)`);
   450â†’
   451â†’    let lastEmit = 0;
   452â†’
   453â†’    const handler = (data) => {
   454â†’      const now = Date.now();
   455â†’
   456â†’      // Throttle to reduce bandwidth
   457â†’      if (now - lastEmit < this.AUDIO_LEVEL_THROTTLE_MS) {
   458â†’        return;
   459â†’      }
   460â†’      lastEmit = now;
   461â†’
   462â†’      // Transform OBS data to our format
   463â†’      const levels = {
   464â†’        timestamp: now,
   465â†’        inputs: data.inputs.map(input => {
   466â†’          // Get max level across all channels for simple display
   467â†’          const allLevels = input.inputLevelsMul.flat();
   468â†’          const maxLevel = Math.max(...allLevels, 0.0001);
   469â†’
   470â†’          return {
   471â†’            inputName: input.inputName,
   472â†’            levelMul: maxLevel,
   473â†’            levelDb: 20 * Math.log10(maxLevel),
   474â†’            channels: input.inputLevelsMul.map(ch => ({
   475â†’              peak: ch[0] || 0,
   476â†’              rms: ch[1] || 0
   477â†’            }))
   478â†’          };
   479â†’        })
   480â†’      };
   481â†’
   482â†’      // Emit to server for forwarding to clients
   483â†’      this.emit('audioLevels', { compId, levels });
   484â†’    };
   485â†’
   486â†’    obs.on('InputVolumeMeters', handler);
   487â†’    this.audioLevelHandlers.set(compId, handler);
   488â†’  }
   489â†’
   490â†’  /**
   491â†’   * Stop forwarding audio levels for a competition
   492â†’   * @private
   493â†’   */
   494â†’  _stopAudioLevelForwarding(compId) {
   495â†’    const obs = this.connections.get(compId);
   496â†’    const handler = this.audioLevelHandlers.get(compId);
   497â†’
   498â†’    if (obs && handler) {
   499â†’      obs.off('InputVolumeMeters', handler);
   500â†’      console.log(`[OBSConnectionManager] Stopped audio level forwarding for ${compId}`);
   501â†’    }
   502â†’
   503â†’    this.audioLevelHandlers.delete(compId);
   504â†’  }
   505â†’
   506â†’  /**
   507â†’   * Shutdown all connections
   508â†’   */
   509â†’  async shutdown() {
   510â†’    console.log('[OBSConnectionManager] Shutting down all connections');
   511â†’
   512â†’    // Stop all audio level forwarding
   513â†’    for (const compId of this.audioLevelHandlers.keys()) {
   514â†’      this._stopAudioLevelForwarding(compId);
   515â†’    }
   516â†’    this.audioLevelSubscriptions.clear();
   517â†’
   518â†’    // Stop all heartbeats
   519â†’    for (const [compId, timer] of this.heartbeatTimers) {
   520â†’      clearInterval(timer);
   521â†’    }
   522â†’    this.heartbeatTimers.clear();
   523â†’
   524â†’    // Clear all reconnect timers
   525â†’    for (const [compId, timer] of this.reconnectTimers) {
   526â†’      clearTimeout(timer);
   527â†’    }
   528â†’    this.reconnectTimers.clear();
   529â†’
   530â†’    // Disconnect all OBS instances
   531â†’    for (const [compId, obs] of this.connections) {
   532â†’      try {
   533â†’        await obs.disconnect();
   534â†’      } catch (e) {
   535â†’        // Ignore disconnect errors
   536â†’      }
   537â†’    }
   538â†’    this.connections.clear();
   539â†’    this.connectionStates.clear();
   540â†’
   541â†’    console.log('[OBSConnectionManager] Shutdown complete');
   542â†’  }
   543â†’}
   544â†’
   545â†’// Singleton instance
   546â†’let instance = null;
   547â†’
   548â†’/**
   549â†’ * Get the OBS Connection Manager singleton
   550â†’ * @returns {OBSConnectionManager}
   551â†’ */
   552â†’export function getOBSConnectionManager() {
   553â†’  if (!instance) {
   554â†’    instance = new OBSConnectionManager();
   555â†’  }
   556â†’  return instance;
   557â†’}
   558â†’
   559â†’export { OBSConnectionManager };
   560â†’export default getOBSConnectionManager;
   561â†’

