/**
 * OBS Connection Manager
 *
 * Manages OBS WebSocket connections to competition VMs.
 * When a client connects for a specific competition, this manager
 * connects to the OBS instance running on that competition's VM.
 *
 * This solves the Mixed Content issue where HTTPS frontends cannot
 * connect directly to HTTP WebSockets on VMs.
 *
 * @module obsConnectionManager
 */

import OBSWebSocket, { EventSubscription } from 'obs-websocket-js';
import { EventEmitter } from 'events';

/**
 * Manages OBS connections per competition
 */
class OBSConnectionManager extends EventEmitter {
  constructor() {
    super();

    // Map of compId -> OBSWebSocket instances
    this.connections = new Map();

    // Map of compId -> connection state
    this.connectionStates = new Map();

    // Reconnect timers per competition
    this.reconnectTimers = new Map();

    // Heartbeat timers per competition
    this.heartbeatTimers = new Map();

    // Heartbeat configuration
    this.HEARTBEAT_INTERVAL = 15000; // 15 seconds
    this.HEARTBEAT_TIMEOUT = 5000; // 5 seconds to respond

    // Audio level subscription tracking
    this.audioLevelSubscriptions = new Map(); // compId -> Set<socketId>
    this.audioLevelHandlers = new Map(); // compId -> handler function
    this.AUDIO_LEVEL_THROTTLE_MS = 66; // ~15fps (OBS sends at ~60fps)

    // Default OBS WebSocket port
    this.OBS_PORT = 4455;

    // Default password (can be overridden)
    this.OBS_PASSWORD = process.env.OBS_WEBSOCKET_PASSWORD || '';

    console.log('[OBSConnectionManager] Initialized');
  }

  /**
   * Connect to OBS on a specific VM for a competition
   * @param {string} compId - Competition ID
   * @param {string} vmAddress - VM address (IP or hostname)
   * @returns {Promise<OBSWebSocket>} The OBS WebSocket connection
   */
  async connectToVM(compId, vmAddress) {
    console.log(`[OBSConnectionManager] Connecting to OBS for competition ${compId} on VM ${vmAddress}`);

    // Check if already connected
    if (this.isConnected(compId)) {
      console.log(`[OBSConnectionManager] Already connected to OBS for ${compId}`);
      return this.connections.get(compId);
    }

    // Parse vmAddress - it might be "ip:port" or just "ip"
    let host = vmAddress;
    if (vmAddress.includes(':')) {
      // vmAddress format is "ip:serverPort" (e.g., "3.89.92.162:3003")
      // We need to use the OBS WebSocket port (4455), not the server port
      host = vmAddress.split(':')[0];
    }

    const obsUrl = `ws://${host}:${this.OBS_PORT}`;
    console.log(`[OBSConnectionManager] OBS WebSocket URL: ${obsUrl}`);

    // Create new OBS WebSocket instance
    const obs = new OBSWebSocket();

    // Set up event handlers
    this._setupEventHandlers(obs, compId, vmAddress);

    try {
      // Connect to OBS with InputVolumeMeters high-volume event subscription
      // InputVolumeMeters is NOT included in EventSubscription.All - must be explicitly requested
      await obs.connect(obsUrl, this.OBS_PASSWORD || undefined, {
        eventSubscriptions: EventSubscription.All | EventSubscription.InputVolumeMeters,
        rpcVersion: 1
      });

      // Store connection
      this.connections.set(compId, obs);
      this.connectionStates.set(compId, {
        connected: true,
        vmAddress: vmAddress,
        connectedAt: new Date().toISOString(),
        error: null
      });

      console.log(`[OBSConnectionManager] Connected to OBS for ${compId} at ${obsUrl}`);

      // Start heartbeat monitoring
      this._startHeartbeat(compId);

      // Emit connection event
      this.emit('connected', { compId, vmAddress });

      return obs;
    } catch (error) {
      console.error(`[OBSConnectionManager] Failed to connect to OBS for ${compId}:`, error.message);

      // Update connection state
      this.connectionStates.set(compId, {
        connected: false,
        vmAddress: vmAddress,
        connectedAt: null,
        error: error.message
      });

      // Schedule reconnect
      this._scheduleReconnect(compId, vmAddress);

      throw error;
    }
  }

  /**
   * Get OBS connection for a competition
   * @param {string} compId - Competition ID
   * @returns {OBSWebSocket|null} The OBS connection or null
   */
  getConnection(compId) {
    return this.connections.get(compId) || null;
  }

  /**
   * Check if connected to OBS for a competition
   * @param {string} compId - Competition ID
   * @returns {boolean} True if connected
   */
  isConnected(compId) {
    const state = this.connectionStates.get(compId);
    return state?.connected === true;
  }

  /**
   * Get connection state for a competition
   * @param {string} compId - Competition ID
   * @returns {Object|null} Connection state or null
   */
  getConnectionState(compId) {
    return this.connectionStates.get(compId) || null;
  }

  /**
   * Disconnect from OBS for a competition
   * @param {string} compId - Competition ID
   */
  async disconnect(compId) {
    console.log(`[OBSConnectionManager] Disconnecting from OBS for ${compId}`);

    // Stop heartbeat
    this._stopHeartbeat(compId);

    // Clear reconnect timer
    const timer = this.reconnectTimers.get(compId);
    if (timer) {
      clearTimeout(timer);
      this.reconnectTimers.delete(compId);
    }

    // Disconnect OBS
    const obs = this.connections.get(compId);
    if (obs) {
      try {
        await obs.disconnect();
      } catch (e) {
        // Ignore disconnect errors
      }
      this.connections.delete(compId);
    }

    // Update state
    this.connectionStates.set(compId, {
      connected: false,
      vmAddress: this.connectionStates.get(compId)?.vmAddress,
      connectedAt: null,
      error: 'Disconnected'
    });

    this.emit('disconnected', { compId });
  }

  /**
   * Set up event handlers for an OBS connection
   * @private
   */
  _setupEventHandlers(obs, compId, vmAddress) {
    // Connection closed
    obs.on('ConnectionClosed', () => {
      console.log(`[OBSConnectionManager] OBS connection closed for ${compId}`);

      // Stop heartbeat
      this._stopHeartbeat(compId);

      this.connectionStates.set(compId, {
        connected: false,
        vmAddress: vmAddress,
        connectedAt: null,
        error: 'Connection closed'
      });

      this.emit('connectionClosed', { compId });

      // Schedule reconnect
      this._scheduleReconnect(compId, vmAddress);
    });

    // Connection error
    obs.on('ConnectionError', (error) => {
      console.error(`[OBSConnectionManager] OBS connection error for ${compId}:`, error);

      this.connectionStates.set(compId, {
        connected: false,
        vmAddress: vmAddress,
        connectedAt: null,
        error: error.message
      });

      this.emit('connectionError', { compId, error: error.message });
    });

    // Identified (successfully authenticated)
    obs.on('Identified', () => {
      console.log(`[OBSConnectionManager] OBS identified for ${compId}`);

      this.connectionStates.set(compId, {
        connected: true,
        vmAddress: vmAddress,
        connectedAt: new Date().toISOString(),
        error: null
      });

      this.emit('identified', { compId });
    });

    // Forward OBS events to listeners
    const eventsToForward = [
      'CurrentProgramSceneChanged',
      'SceneListChanged',
      'InputVolumeChanged',
      'InputMuteStateChanged',
      'StreamStateChanged',
      'RecordStateChanged',
      'StudioModeStateChanged',
      'CurrentPreviewSceneChanged'
    ];

    for (const eventName of eventsToForward) {
      obs.on(eventName, (data) => {
        this.emit('obsEvent', { compId, eventName, data });
      });
    }
  }

  /**
   * Schedule a reconnection attempt
   * @private
   */
  _scheduleReconnect(compId, vmAddress) {
    // Clear existing timer
    const existingTimer = this.reconnectTimers.get(compId);
    if (existingTimer) {
      clearTimeout(existingTimer);
    }

    // Schedule reconnect after 30 seconds
    const timer = setTimeout(async () => {
      console.log(`[OBSConnectionManager] Attempting reconnection for ${compId}`);
      try {
        await this.connectToVM(compId, vmAddress);
      } catch (error) {
        console.error(`[OBSConnectionManager] Reconnection failed for ${compId}:`, error.message);
        // _scheduleReconnect will be called again from the error handler
      }
    }, 30000);

    this.reconnectTimers.set(compId, timer);
  }

  /**
   * Start heartbeat monitoring for a connection
   * Pings OBS every HEARTBEAT_INTERVAL ms to detect dead connections
   * @private
   */
  _startHeartbeat(compId) {
    // Stop any existing heartbeat
    this._stopHeartbeat(compId);

    const obs = this.connections.get(compId);
    if (!obs) {
      return;
    }

    console.log(`[OBSConnectionManager] Starting heartbeat for ${compId} (interval: ${this.HEARTBEAT_INTERVAL}ms)`);

    const heartbeat = setInterval(async () => {
      const currentObs = this.connections.get(compId);
      if (!currentObs || !this.isConnected(compId)) {
        this._stopHeartbeat(compId);
        return;
      }

      try {
        // Create a timeout promise
        const timeoutPromise = new Promise((_, reject) => {
          setTimeout(() => reject(new Error('Heartbeat timeout')), this.HEARTBEAT_TIMEOUT);
        });

        // Race between the OBS call and timeout
        await Promise.race([
          currentObs.call('GetVersion'),
          timeoutPromise
        ]);

        // Heartbeat successful - connection is alive
      } catch (error) {
        console.warn(`[OBSConnectionManager] Heartbeat failed for ${compId}: ${error.message}`);

        // Stop heartbeat before triggering disconnect
        this._stopHeartbeat(compId);

        // Get vmAddress for reconnection
        const state = this.connectionStates.get(compId);
        const vmAddress = state?.vmAddress;

        // Update connection state
        this.connectionStates.set(compId, {
          connected: false,
          vmAddress: vmAddress,
          connectedAt: null,
          error: 'Connection lost (heartbeat failed)'
        });

        // Remove the dead connection
        this.connections.delete(compId);

        // Emit disconnect event
        console.log(`[OBSConnectionManager] Connection dead for ${compId}, triggering reconnect`);
        this.emit('connectionClosed', { compId });

        // Schedule reconnect if we have the VM address
        if (vmAddress) {
          this._scheduleReconnect(compId, vmAddress);
        }
      }
    }, this.HEARTBEAT_INTERVAL);

    this.heartbeatTimers.set(compId, heartbeat);
  }

  /**
   * Stop heartbeat monitoring for a connection
   * @private
   */
  _stopHeartbeat(compId) {
    const timer = this.heartbeatTimers.get(compId);
    if (timer) {
      clearInterval(timer);
      this.heartbeatTimers.delete(compId);
      console.log(`[OBSConnectionManager] Stopped heartbeat for ${compId}`);
    }
  }

  // =============================================
  // Audio Level Subscription Methods (Phase 2)
  // =============================================

  /**
   * Subscribe a socket to receive audio level updates for a competition
   * @param {string} compId - Competition ID
   * @param {string} socketId - Socket ID of the subscriber
   */
  subscribeAudioLevels(compId, socketId) {
    if (!this.audioLevelSubscriptions.has(compId)) {
      this.audioLevelSubscriptions.set(compId, new Set());
    }
    this.audioLevelSubscriptions.get(compId).add(socketId);

    console.log(`[OBSConnectionManager] Audio level subscription added: ${socketId} -> ${compId} (total: ${this.audioLevelSubscriptions.get(compId).size})`);

    // Start forwarding if this is the first subscriber
    if (this.audioLevelSubscriptions.get(compId).size === 1) {
      this._startAudioLevelForwarding(compId);
    }
  }

  /**
   * Unsubscribe a socket from audio level updates
   * @param {string} compId - Competition ID
   * @param {string} socketId - Socket ID to unsubscribe
   */
  unsubscribeAudioLevels(compId, socketId) {
    const subs = this.audioLevelSubscriptions.get(compId);
    if (subs) {
      subs.delete(socketId);
      console.log(`[OBSConnectionManager] Audio level subscription removed: ${socketId} -> ${compId} (remaining: ${subs.size})`);

      // Stop forwarding if no more subscribers
      if (subs.size === 0) {
        this._stopAudioLevelForwarding(compId);
        this.audioLevelSubscriptions.delete(compId);
      }
    }
  }

  /**
   * Unsubscribe a socket from all competitions (called on disconnect)
   * @param {string} socketId - Socket ID to unsubscribe
   */
  unsubscribeAudioLevelsAll(socketId) {
    for (const [compId, subs] of this.audioLevelSubscriptions) {
      if (subs.has(socketId)) {
        this.unsubscribeAudioLevels(compId, socketId);
      }
    }
  }

  /**
   * Start forwarding audio levels from OBS to subscribers
   * Throttles 60fps from OBS down to ~15fps
   * @private
   */
  _startAudioLevelForwarding(compId) {
    const obs = this.connections.get(compId);
    if (!obs) {
      console.warn(`[OBSConnectionManager] Cannot start audio forwarding: no connection for ${compId}`);
      return;
    }

    // Don't start if already forwarding
    if (this.audioLevelHandlers.has(compId)) {
      return;
    }

    console.log(`[OBSConnectionManager] Starting audio level forwarding for ${compId} (throttle: ${this.AUDIO_LEVEL_THROTTLE_MS}ms)`);

    let lastEmit = 0;

    const handler = (data) => {
      const now = Date.now();

      // Throttle to reduce bandwidth
      if (now - lastEmit < this.AUDIO_LEVEL_THROTTLE_MS) {
        return;
      }
      lastEmit = now;

      // Transform OBS data to our format
      const levels = {
        timestamp: now,
        inputs: data.inputs.map(input => {
          // Get max level across all channels for simple display
          const allLevels = input.inputLevelsMul.flat();
          const maxLevel = Math.max(...allLevels, 0.0001);

          return {
            inputName: input.inputName,
            levelMul: maxLevel,
            levelDb: 20 * Math.log10(maxLevel),
            channels: input.inputLevelsMul.map(ch => ({
              peak: ch[0] || 0,
              rms: ch[1] || 0
            }))
          };
        })
      };

      // Emit to server for forwarding to clients
      this.emit('audioLevels', { compId, levels });
    };

    obs.on('InputVolumeMeters', handler);
    this.audioLevelHandlers.set(compId, handler);
  }

  /**
   * Stop forwarding audio levels for a competition
   * @private
   */
  _stopAudioLevelForwarding(compId) {
    const obs = this.connections.get(compId);
    const handler = this.audioLevelHandlers.get(compId);

    if (obs && handler) {
      obs.off('InputVolumeMeters', handler);
      console.log(`[OBSConnectionManager] Stopped audio level forwarding for ${compId}`);
    }

    this.audioLevelHandlers.delete(compId);
  }

  /**
   * Shutdown all connections
   */
  async shutdown() {
    console.log('[OBSConnectionManager] Shutting down all connections');

    // Stop all audio level forwarding
    for (const compId of this.audioLevelHandlers.keys()) {
      this._stopAudioLevelForwarding(compId);
    }
    this.audioLevelSubscriptions.clear();

    // Stop all heartbeats
    for (const [compId, timer] of this.heartbeatTimers) {
      clearInterval(timer);
    }
    this.heartbeatTimers.clear();

    // Clear all reconnect timers
    for (const [compId, timer] of this.reconnectTimers) {
      clearTimeout(timer);
    }
    this.reconnectTimers.clear();

    // Disconnect all OBS instances
    for (const [compId, obs] of this.connections) {
      try {
        await obs.disconnect();
      } catch (e) {
        // Ignore disconnect errors
      }
    }
    this.connections.clear();
    this.connectionStates.clear();

    console.log('[OBSConnectionManager] Shutdown complete');
  }
}

// Singleton instance
let instance = null;

/**
 * Get the OBS Connection Manager singleton
 * @returns {OBSConnectionManager}
 */
export function getOBSConnectionManager() {
  if (!instance) {
    instance = new OBSConnectionManager();
  }
  return instance;
}

export { OBSConnectionManager };
