  const obsConnected = obsState?.connected ?? false;

  // Subscribe to socket events
  useEffect(() => {
    if (!socket || !connected) {
      console.log('OBSContext: Socket not available or not connected');
      return;
    }

    console.log('OBSContext: Setting up OBS event listeners');

