      console.log('OBSContext: Preview scene changed', data);
      setObsState(prev => ({
        ...prev,
        previewScene: data.sceneName
      }));
    };

    const handleStreamingStateChanged = (data) => {
      console.log('OBSContext: Streaming state changed', data);
      setObsState(prev => ({
        ...prev,
        streaming: data.active

