        return res.status(503).json({
          error: 'Alert service not initialized',
          details: 'Firebase credentials not configured'
        });
      }
    }

    const alert = await alertService.createAlert(compId, {
      level,
      category,
      title,
      message,
      sourceId: sourceId || null,
      metadata: metadata || {}
