     1â†’import { useState, useEffect } from 'react';
     2â†’import {
     3â†’  EyeIcon,
     4â†’  PencilIcon,
     5â†’  DocumentDuplicateIcon,
     6â†’  TrashIcon,
     7â†’  RectangleStackIcon,
     8â†’  PlusIcon,
     9â†’  XMarkIcon,
    10â†’  Cog6ToothIcon,
    11â†’  Bars3Icon,
    12â†’  DocumentTextIcon
    13â†’} from '@heroicons/react/24/outline';
    14â†’import { useOBS } from '../../context/OBSContext';
    15â†’import { useShow } from '../../context/ShowContext';
    16â†’
    17â†’/**
    18â†’ * SceneList - Display and manage OBS scenes
    19â†’ * Groups scenes by category and provides actions: Preview, Edit, Duplicate, Delete
    20â†’ */
    21â†’export default function SceneList({ onEditScene, onSceneAction }) {
    22â†’  const { obsState, obsConnected, switchScene, setPreviewScene, createScene, deleteScene, deleteAllScenes, refreshState, reorderScenes } = useOBS();
    23â†’  const { socketUrl } = useShow();
    24â†’  const [expandedCategories, setExpandedCategories] = useState({
    25â†’    'generated-single': true,
    26â†’    'generated-multi': true,
    27â†’    'static': true,
    28â†’    'graphics': true,
    29â†’    'manual': true,
    30â†’    'template': true
    31â†’  });
    32â†’  const [showCreateModal, setShowCreateModal] = useState(false);
    33â†’  const [newSceneName, setNewSceneName] = useState('');
    34â†’  const [isCreating, setIsCreating] = useState(false);
    35â†’  const [draggedScene, setDraggedScene] = useState(null);
    36â†’
    37â†’  // Delete confirmation popover state (inline, positioned near the delete button)
    38â†’  const [deletePopoverScene, setDeletePopoverScene] = useState(null);
    39â†’  const [isDeleting, setIsDeleting] = useState(false);
    40â†’
    41â†’  // Delete all scenes modal state
    42â†’  const [showDeleteAllModal, setShowDeleteAllModal] = useState(false);
    43â†’  const [isDeletingAll, setIsDeletingAll] = useState(false);
    44â†’
    45â†’  // Template-related state
    46â†’  const [createMode, setCreateMode] = useState('blank'); // 'blank' or 'template'
    47â†’  const [templates, setTemplates] = useState([]);
    48â†’  const [selectedTemplateId, setSelectedTemplateId] = useState('');
    49â†’  const [isLoadingTemplates, setIsLoadingTemplates] = useState(false);
    50â†’
    51â†’  // Fetch templates when modal opens
    52â†’  useEffect(() => {
    53â†’    if (showCreateModal && socketUrl) {
    54â†’      setIsLoadingTemplates(true);
    55â†’      fetch(`${socketUrl}/api/obs/templates`)
    56â†’        .then(res => res.json())
    57â†’        .then(data => {
    58â†’          const templateList = Array.isArray(data) ? data : (data.templates || []);
    59â†’          setTemplates(templateList);
    60â†’        })
    61â†’        .catch(err => {
    62â†’          console.error('Failed to fetch templates:', err);
    63â†’          setTemplates([]);
    64â†’        })
    65â†’        .finally(() => {
    66â†’          setIsLoadingTemplates(false);
    67â†’        });
    68â†’    }
    69â†’  }, [showCreateModal, socketUrl]);
    70â†’
    71â†’  // Get scenes from obsState
    72â†’  const scenes = obsState?.scenes || [];
    73â†’  const currentScene = obsState?.currentScene;
    74â†’  const previewScene = obsState?.previewScene;
    75â†’  const studioModeEnabled = obsState?.studioModeEnabled || false;
    76â†’
    77â†’  // Category configuration
    78â†’  const categories = [
    79â†’    { id: 'generated-single', label: 'Generated Single', color: 'purple' },
    80â†’    { id: 'generated-multi', label: 'Generated Multi', color: 'blue' },
    81â†’    { id: 'static', label: 'Static', color: 'green' },
    82â†’    { id: 'graphics', label: 'Graphics', color: 'yellow' },
    83â†’    { id: 'manual', label: 'Manual', color: 'gray' },
    84â†’    { id: 'template', label: 'Template', color: 'pink' }
    85â†’  ];
    86â†’
    87â†’  // Group scenes by category
    88â†’  // Note: We trust the backend category and don't infer from scene names anymore.
    89â†’  // The coordinator (broadcastOBSState) sets the category based on:
    90â†’  // - Scene naming conventions (e.g., "Single - ", "Dual - ")
    91â†’  // - Template scenes stored in Firebase (competitions/{compId}/obs/templateScenes)
    92â†’  const groupedScenes = categories.map(category => {
    93â†’    const categoryScenes = scenes.filter(scene => {
    94â†’      // Trust the backend category, default to 'manual' if not set
    95â†’      const sceneCategory = scene.category || 'manual';
    96â†’      return sceneCategory === category.id;
    97â†’    });
    98â†’
    99â†’    return {
   100â†’      ...category,
   101â†’      scenes: categoryScenes,
   102â†’      count: categoryScenes.length
   103â†’    };
   104â†’  }).filter(cat => cat.count > 0); // Only show categories with scenes
   105â†’
   106â†’  // Toggle category expansion
   107â†’  const toggleCategory = (categoryId) => {
   108â†’    setExpandedCategories(prev => ({
   109â†’      ...prev,
   110â†’      [categoryId]: !prev[categoryId]
   111â†’    }));
   112â†’  };
   113â†’
   114â†’  // Handle scene actions
   115â†’  const handlePreview = (sceneName) => {
   116â†’    if (studioModeEnabled) {
   117â†’      setPreviewScene(sceneName);
   118â†’    } else {
   119â†’      // If studio mode is disabled, switch to scene directly
   120â†’      switchScene(sceneName);
   121â†’    }
   122â†’    if (onSceneAction) {
   123â†’      onSceneAction('preview', sceneName);
   124â†’    }
   125â†’  };
   126â†’
   127â†’  const handleEdit = (sceneName) => {
   128â†’    if (onEditScene) {
   129â†’      onEditScene(sceneName);
   130â†’    }
   131â†’  };
   132â†’
   133â†’  const handleRename = (sceneName) => {
   134â†’    if (onSceneAction) {
   135â†’      onSceneAction('rename', sceneName);
   136â†’    }
   137â†’  };
   138â†’
   139â†’  const handleDuplicate = (sceneName) => {
   140â†’    if (onSceneAction) {
   141â†’      onSceneAction('duplicate', sceneName);
   142â†’    }
   143â†’  };
   144â†’
   145â†’  const handleDelete = (sceneName) => {
   146â†’    // Toggle inline delete popover for this scene
   147â†’    if (deletePopoverScene === sceneName) {
   148â†’      setDeletePopoverScene(null);
   149â†’    } else {
   150â†’      setDeletePopoverScene(sceneName);
   151â†’    }
   152â†’  };
   153â†’
   154â†’  const confirmDelete = async (sceneName) => {
   155â†’    if (!sceneName) return;
   156â†’
   157â†’    setIsDeleting(true);
   158â†’    try {
   159â†’      deleteScene(sceneName);
   160â†’      // Notify parent if needed
   161â†’      if (onSceneAction) {
